			CREATE TABLE `express_sys_monitor` (
                  `id` bigint(11) NOT NULL AUTO_INCREMENT,
                  `optime` varchar(20) COLLATE utf8_bin DEFAULT '',
                  `type` varchar(20) DEFAULT '',
                  `dealflag` int(2) DEFAULT '0',  
                   PRIMARY KEY (`id`)
             ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;
             
