mapper.select:
		select * from EXPRESS_OPS_APPLYEDITCARTYPE where 1=1

             and  cwb = #{cwb}


             and  cwb in (${cwbs})


             and review_status = #{reviewStatus} 


             and  apply_userid  in (${applyUserId}) 


             and apply_branchid in  (${branchId})  


             and review_status > 0


             and review_status = 0


             and  apply_time >= #{startApplyTime}


             and  apply_time <= #{endApplyTime}

        order by apply_time asc
